<template>    <div class="calendar-con">        <van-calendar v-model="show" type="range" :max-range="15" @confirm = "onConfirm"></van-calendar>    </div></template><script>    import {Calendar} from 'vant';    export default {        name: "Calendar",        components:{            'van-calendar' :Calendar,        },        data(){            return {                show:true,            }        },        methods:{            onConfirm(date){                let[startTime,endTime] = date;                let start = startTime.getMonth()+1+'.'+startTime.getDate();                let end = endTime.getMonth()+1+'.'+endTime.getDate();                let startDay = startTime.getDay();                let endDay = endTime.getDay();                let stayDay = (endTime.getTime()-startTime.getTime())/1000/60/60/24;                this.$store.commit('setTime',{start,end,startDay,endDay,stayDay})                // console.log(date);                 this.$router.back();                ///this.$router.push({path:'/'});            }        }    }</script><style>    .calendar-con .van-icon-cross::before {        content: '';    }</style><style scoped></style>}