<template>    <div>      <header class="collection-head">          <router-link to="/center" class="iconfont icon-zuojiantou"></router-link>          <div class="collection-title">              收藏管理          </div>      </header>        <main class="coll-main-con">            <div v-for="item in homestay" :key="item">            <router-link :to="{path:'/detailh',query:{'hid':item.hid}}">            <van-card                    :price="item.hprice"                    :desc="item.hcity"                    :title="item.hname"                    :thumb="item.hthumb"            />            </router-link>            </div>        </main>    </div></template><script>    import { Card,Toast } from 'vant';    import 'vant/lib/card/style'    import 'vant/lib/toast/style'    import {apiInitCollection} from "../../http/api";    import {IMGURL, SUCCESS} from "../../lib/base";    export default {        name: "Collection",        components:{            VanCard:Card        },        data(){            return{                homestay:[],            }        },        computed:{            collection() {                return this.$store.state.conllection;            },        },        methods:{            initCollection(){                apiInitCollection(this.collection).then(res=>{                   if(res.code === SUCCESS){                      this.homestay =  res.data.map(ele=>{                           ele.hthumb = IMGURL+ele.hthumb;                           ele.hcity = ele.hcity +'·'+ele.harea;                           return ele;                       })                   }else{                       Toast(res.msg);                   }                }).catch(()=>{                })            }        },        mounted() {            this.initCollection();        }    }</script><style src="../../style/collection.css" scoped></style>