<template>    <div>        <div class="index-swipe-con">        <van-swipe :autoplay="3000" class="my-swipe" indicator-color="white">            <van-swipe-item><img alt="" src="../../assets/firstpage/banner7.png"></van-swipe-item>            <van-swipe-item><img alt="" src="../../assets/firstpage/banner1.jpg"></van-swipe-item>            <van-swipe-item><img alt="" src="../../assets/firstpage/banner2.jpg"></van-swipe-item>            <van-swipe-item><img alt="" src="../../assets/firstpage/banner4.jpg"></van-swipe-item>            <van-swipe-item><img alt="" src="../../assets/firstpage/banner5.jpg"></van-swipe-item>            <van-swipe-item><img alt="" src="../../assets/firstpage/banner6.jpg"></van-swipe-item>        </van-swipe>        </div>        <div class="city">            <div class="city-inner">                <div class="city-choose">                    <div class="city-choose-left">                       <router-link to="/province">{{indexSearch.province}}</router-link>                        <img src="../../assets/index/city-select.png" alt="">                    </div>                    <div class="city-input">                        <input v-model="search" type="text" placeholder="景点 地址 关键词">                    </div>                    <div class="city-choose-right">                        <img src="../../assets/firstpage/position.png" alt="">                        <p>定位</p>                    </div>                </div>                <router-link to="/calendar" class="city-time" tag="div">                    <div class="city-time-left">                        <p>{{indexSearch.startTime}}</p>                        <p>{{indexSearch.startDay}}</p>                        <img src="../../assets/firstpage/箭头.png" alt="">                        <p class="p-thirdChild">{{indexSearch.endTime}}</p>                        <p class="p-fourthChild">{{indexSearch.endDay}}</p>                    </div>                    <div class="city-time-right">                        <p>共{{indexSearch.stayDay}}晚</p>                    </div>                </router-link>                <router-link :to="{path:'/list',query:{search:search}}" class="search" tag="div">                    <b>立即查找</b>                </router-link>                <div class="city-bottom">                    <div class="city-bottom-item">                        <img src="../../assets/firstpage/文件.png" alt="">                        <span>信用免押金</span>                    </div>                    <div class="city-bottom-item">                        <img src="../../assets/firstpage/耳机.png" alt="">                        <span>24小时客服</span>                    </div>                    <div class="city-bottom-item">                        <img src="../../assets/firstpage/盾牌.png" alt="">                        <span>房源房东真实认证</span>                    </div>                </div>            </div>        </div>        <div class="footer">            <tabbar :index="1"></tabbar>        </div>    </div></template><script>    import tabbar from "../tabbar/tabbar";    export default {        name: "index",        data(){            return{                search:'',            }        },        components:{            tabbar        },        computed:{            indexSearch(){                return this.$store.state.indexSearch;            },        },        methods:{            initSearch(){                this.$store.dispatch('setTime');                this.$store.dispatch('setProvince','太原');            }        },        mounted() {            //比较对象是否相等            if(JSON.stringify(this.$store.state.indexSearch.province)===JSON.stringify('')){            //数据为空才执行            this.initSearch();            }        }    }</script><style scoped>    @import "~u-reset.css";    @import "../../style/index.css";    .my-swipe {        height: 5.79rem;        position: relative;    }    .my-swipe .van-swipe-item {        color: #fff;        font-size: 20px;        line-height: 150px;        text-align: center;        background-color: #39a9ed;    }    .footer{        position: fixed;        left: 0;        bottom: 0;        width: 100%;        z-index: 99999;    }</style>