rovince(city.city)"                      :title="city.city"/>            <van-index-anchor index="Q"/>            <van-cell v-for="(city,index) in province['Q']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="R"/>            <van-cell v-for="(city,index) in province['R']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="S"/>            <van-cell v-for="(city,index) in province['S']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="T"/>            <van-cell v-for="(city,index) in province['T']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="U"/>            <van-cell v-for="(city,index) in province['U']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="V"/>            <van-cell v-for="(city,index) in province['V']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="W"/>            <van-cell v-for="(city,index) in province['W']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="X"/>            <van-cell v-for="(city,index) in province['X']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="Y"/>            <van-cell v-for="(city,index) in province['Y']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>            <van-index-anchor index="Z"/>            <van-cell v-for="(city,index) in province['Z']" :key="index" @click="handleProvince(city.city)"                      :title="city.city"/>        </van-index-bar>    </div></template><script>    import city from '../../lib/city.json'    export default {        name: "province",        data() {            return {                province: {},            }        },        methods: {            initProvince() {                city.province.forEach(ele => {                    let firstChar = ele.en.charAt(0).toUpperCase();                    if (!this.province[firstChar]) {                        this.$set(this.province, firstChar, []);                        //后加的属性 vue 不会响应的                        // this.province[firstChar] = [];                    }                    this.province[firstChar].push(ele);                })            },            handleProvince(province) {                this.$store.commit('setProvince', province);                this.$router.back();            }        },        computed: {            // A B C D 键值排序            provinceKeys() {                let arr = [];                //当省份不是空对象的时候 stringify 判定两个对象是否相等                if (JSON.stringify(this.province) != '{}') {                    arr = Object.keys(this.province).sort();                }                return arr;            }        },        mounted() {            //console.log(city);            this.initProvince();        }    }</script><style>    .province-con .van-index-anchor{           background-color:  rgba(0,0,0,0.03);        color: #07c160;        }</style><style scoped></style>